							<Grid item>
								<Box mb={5}>
									<NavigationBar props={sectionInView}/>
								</Box>
							</Grid>
							<Grid item>
								<Carousel/>
							</Grid>
							<Grid item>
								<Box m={5}>
									<Button variant="contained" color="primary">
										Primary Theme
									</Button>
									<Button variant="contained" color="secondary">
										Secondary Theme
									</Button>
								</Box>
							</Grid>
							{/* Navbar (Logo, items, register/login) */}
							{/* Picture carousel (Tagline, org by) */}
							{/* Sponsered by */}
