				{/*<VisibilitySensor onChange={(event) => onChange(event, "Papers")}>*/}
				{/*	<div>/!* Call for papers *!/</div>*/}
				{/*</VisibilitySensor>*/}
				{/*<VisibilitySensor onChange={(event) => onChange(event, "Details")}>*/}
