				{/*</VisibilitySensor>*/}
				{/*<VisibilitySensor onChange={(event) => onChange(event, "Contact")}>*/}
				{/*	<div>/!* Contact us (Footer) *!/</div>*/}
				{/*</VisibilitySensor>*/}
			</Grid>


		</div>
	);
}

export default Home
